INSERT INTO `base_legal` VALUES (1,'Não há previsão na legislação para a retenção, As legislações que tratam de quando há retenção não mencionam este serviço.',14),(2,'Art. 2º do Decreto-Lei nº 2.030/1983; Art. 1º, inciso III, do Decreto-Lei nº 2.065/1983; Art. 52 da Lei nº 7.450/1985; 11.196/2005. Art. 714 do RIR/2018.',11),(3,'Art. 30 a 36 da Lei nº 10.833/2003; Instrução Normativa SRF nº 459/2004 e Solução de Consulta COSIT nº 407/2017.',11),(4,'Art. 31 da Lei nº 8.212/1991, art. 219 do RPS, aprovado pelo Decreto nº 3.048/1999, e arts. 108 a 145 e 167 da Instrução Normativa RFB nº 2.110/2022.',10),(5,'Decreto nº 3.048/1999, e art. 111 da Instrução Normativa RFB nº 2.110/2022.',3),(6,'Art. 2º do Decreto-Lei nº 2.030/1983; Art. 1º, inciso III, do Decreto-Lei nº 2.065/1983; Art. 52 da Lei nº 7.450/1985; Art. 6º da Lei nº 9.064/1995; Art. 714 do RIR/2018; Art. 70, inciso I, alínea d, da Lei nº 11.196/2005.',4),(7,'Art. 31 da Lei nº 8.212/1991, Art. 219 do RPS, aprovado pelo Decreto nº 3.048/1999; Arts. 108 a 145 e 167 da Instrução Normativa RFB nº 2.110/2022.',4),(8,'Art. 2º do Decreto-Lei nº 2.030/1983; Art. 1º, inciso III, do Decreto-Lei nº 2.065/1983;  Art. 714 do RIR/2018; Art. 70, inciso I, alínea d, da Lei nº 11.196/2005.',5),(9,'Art. 30 a 36 da Lei 10.833/2003; Instrução Normativa SRF nº 459/2004.',5),(10,'Art. 111 e Anexo VI da Instrução Normativa RFB nº 2.110/2022.',5),(11,'Art. 130, § 1º da Instrução Normativa RFB nº 2.110/2022, Art. 31 da Lei nº 8.212/1991; Arts. 13 e 18 da Lei Complementar nº 123/2006; Arts. 7º a 9º da Lei nº 12.546/2011.',6),(12,'Art. 3º da Lei Complementar 116/2003.',6),(13,'Art. 31 da Lei nº 8.212/1991, Art. 219 do RPS, aprovado pelo Decreto nº 3.048/1999; Arts. 108 a 145 e 167 da Instrução Normativa RFB nº 2.110/2022. ',7),(14,'Art. 53 da Lei nº 7.450/1985; Art. 8º do Decreto-Lei nº 2.287/1986; Art. 6º da Lei nº 9.064/1995; Art. 718, inciso I, do RIR/2018; Art. 70, inciso I, alínea d, da Lei nº 11.196/2005.',8),(15,'Art. 3º do Decreto